import React, { useEffect } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { acGetItemPoll } from "../../redux/action";

Vote.propTypes = {};

function Vote(props) {
  useEffect(() => {
    const id = props.match.match.params.id;
    props.showPoll(id);
  }, []);

  const showVote = () => {
    console.log(props.pollItem);
    return (
      <div>
        <p>{props.pollItem.content}</p>
        <div>
          {props.pollItem.poll_question ? (
            props.pollItem.poll_question.map((item, i) => {
              return <p>{item.content}</p>;
            })
          ) : (
            <p>accc</p>
          )}
        </div>
      </div>
    );
  };
  return <div>{showVote()}</div>;
}

const mapStateToProps = (state) => {
  return {
    pollItem: state.poll_item,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    showPoll: (id) => {
      return dispatch(acGetItemPoll(id));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Vote);
