import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import Axios from "axios";

Detail.propTypes = {};

function Detail(props) {
  const [item, setItem] = useState();

  useEffect(() => {
    const token = localStorage.getItem("token");
    const id = props.match.match.params.id;
    async function fetchDetailPoll() {
      const data = await Axios.get(
        `https://dev.miagi-so.net/api/v1/poll/${id}/show`,
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      )
        .then((res) => {
          return res.data;
        })
        .catch((err) => console.log(err));
      setItem(data);
    }
    fetchDetailPoll();
  }, []);

  const showPollQuestion = (poll_question) => {
    const index = 0;
    const total_poll_answer = poll_question.map((a) => {
      return (index += a.poll_answer.length);
    });

    return poll_question.map((poll_item) => {
      return <h1>{poll_item.content}</h1>;
    });
  };

  const showItem = () => {
    if (item) {
      return (
        <div>
          <h2>{item.content}</h2>
          <div>{showPollQuestion(item.poll_question)}</div>
        </div>
      );
    }
  };
  return <div>{showItem()}</div>;
}

export default Detail;
