import React, { useEffect } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { acGetItemPoll } from "../../redux/action";
import "./style.scss";

Vote.propTypes = {};

function Vote(props) {
  useEffect(() => {
    const id = props.match.match.params.id;
    props.showPoll(id);
  }, []);

  const showVote = () => {
    return (
      <div className="vote__main">
        <p className="vote__title">{props.pollItem.content}</p>
        <div>
          {props.pollItem.poll_question ? (
            props.pollItem.poll_question.map((item, i) => {
              return (
                <div className="vote__item">
                  <div className="vote__check">
                    <i
                      className="fa fa-check vote__icon"
                      aria-hidden="true"
                    ></i>
                  </div>
                  <p>{item.content}</p>
                </div>
              );
            })
          ) : (
            <p>accc</p>
          )}
        </div>
      </div>
    );
  };
  return <div className="vote">{showVote()}</div>;
}

const mapStateToProps = (state) => {
  return {
    pollItem: state.poll_item,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    showPoll: (id) => {
      return dispatch(acGetItemPoll(id));
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Vote);
