import React, { useState } from "react";
import PropTypes from "prop-types";
import "./style.scss";
import { acLoginRq } from "../../redux/action";

Home.propTypes = {};

function Home(props) {
  const [valueForm, setValueForm] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    console.log(e.target.value);
    const name = e.target.name;
    const value = e.target.value;
    setValueForm({ [name]: value });
  };

  const onSubmit = () => {};

  return (
    <div className="home">
      <form onSubmit={onSubmit} className="home__form">
        <i class="home__icon fa fa-user" aria-hidden="true"></i>
        <input
          placeholder="Email"
          className="home__input"
          name="email"
          value={valueForm.email}
          onChange={handleChange}
        ></input>
        <i class="home__icon fa fa-key" aria-hidden="true"></i>
        <input
          placeholder="Password"
          className="home__input"
          name="password"
          value={valueForm.password}
          onChange={handleChange}
        ></input>
        <button type="submit" className="home__btn">
          Login
        </button>
      </form>
    </div>
  );
}

const mapDispatchToProps = (dispatch) => {
  return {
    loginRq: (data) => {
      dispatch(acLoginRq(data));
    },
  };
};
export default Home;
