import React, { useEffect, useState } from "react";
import PropTypes from "prop-types";
import { acGetItemPoll } from "../../redux/action";
import { connect } from "react-redux";
import "./style.scss";

Edit.propTypes = {};

function Edit(props) {
  useEffect(() => {
    const id = props.match.match.params.id;
    props.getItemPoll(id);
  }, []);

  const [valueInput, setValueInput] = useState("");

  const showItemQuestionPoll = () => {
    console.log(props.poll_item);
    if (props.poll_item) {
      if (props.poll_item.poll_question) {
        return props.poll_item.poll_question.map((item, i) => {
          return (
            <div className="edit__main-item">
              <p className="edit__main-name" key={i}>
                {item.content}
              </p>
              <i class="fa fa-times edit__main-icon" aria-hidden="true"></i>
            </div>
          );
        });
      }
    }
  };
  return (
    <div className="edit">
      <p className="edit__content">What need to be poll ? </p>
      <div className="edit__main">
        <div className="edit__main-control">
          <input className="edit__main-input" placeholder="Add option"></input>
          <button className="edit__main-btn">Add</button>
        </div>
        <div>{showItemQuestionPoll()}</div>
      </div>
      <button className="edit__btn">Create</button>
    </div>
  );
}

const mapDispatchToProps = (dispatch) => {
  return {
    getItemPoll: (id) => {
      return dispatch(acGetItemPoll(id));
    },
  };
};
const mapStateToProps = (state) => {
  return {
    poll_item: state.poll_item,
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Edit);
