import React, { useEffect } from "react";
import PropTypes from "prop-types";
import { getPollRq } from "../../redux/action";
import { connect } from "react-redux";
import "./style.scss";

Poll.propTypes = {};

function Poll(props) {
  useEffect(() => props.getPoll(), []);
  const showListPoll = () => {
    if (props.poll.length > 0) {
      console.log(props.poll[0]);
      return props.poll[0].map((item, i) => {
        return (
          <div className="poll__item" key={i}>
            <h2>{item.content}</h2>
          </div>
        );
      });
    }
  };

  return <div className="poll">{showListPoll()}</div>;
}

const mapStateToProps = (state) => {
  return {
    poll: state.poll,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    getPoll: () => {
      dispatch(getPollRq());
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Poll);
