import * as Types from "../constant/index";
var initialState = [];

const poll = (state = initialState, action) => {
  switch (action.type) {
    case Types.GET_DATA_SUCCES:
      return [...state, action.payload];
    case Types.ADD_POLL_SUCCES:
      state.push(action.payload);
      return [...state];
    default:
      return state;
  }
};
export default poll;
