import * as Types from "../constant/index";
var initialState = [];

const poll = (state = initialState, action) => {
  switch (action.type) {
    case Types.GET_DATA_SUCCES:
      return [action.payload];
    case Types.ADD_POLL_SUCCES:
      return [state.unshift(action.payload), ...state];
    case Types.ADD_POLL_ITEM_SUCCES:
      const id = parseInt(action.id);
      const index = findIndex(state, id);
      console.log(state);
    default:
      return state;
  }
};

const findIndex = (arr, id) => {
  if (arr.length > 0) {
    for (let i = 0; i <= arr.length; i++) {
      if (arr[i].id === id) {
        return i;
      }
    }
  }
};
export default poll;
