import React, { useEffect } from "react";
import PropTypes from "prop-types";
import { acGetItemPoll } from "../../redux/action";
import { connect } from "react-redux";

Edit.propTypes = {};

function Edit(props) {
  useEffect(() => {
    const id = props.match.match.params.id;
    props.getItemPoll(id);
  }, []);
  return (
    <div>
      <p>What need to be poll ? </p>
      <div>
        <input placeholder="Add option"></input>
        <button>Add</button>
        <div>{showItemQuestionPoll()}</div>
      </div>
    </div>
  );
}

// const mapStateToProps = () => {

// }

const mapDispatchToProps = (dispatch) => {
  return {
    getItemPoll: (id) => {
      return dispatch(acGetItemPoll(id));
    },
  };
};
const mapStateToProps = (state) => {
  return {
    poll_item: state.poll_item,
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Edit);
