import * as Types from "../constant/index";
var initialState = {
  isLogin: false,
  avatar: "",
  err: "",
};

const user = (state = initialState, action) => {
  switch (action.type) {
    case Types.LOGIN_SUCCES:
      return {
        ...state,
        isLogin: true,
      };
    case Types.LOGIN_FAIL:
      // console.log(action.err);
      return {
        ...state,
        isLogin: false,
        err: action.err,
      };
    case Types.LOGGED_IN:
      return {
        ...state,
        isLogin: true,
      };
    case Types.LOGIN_FAIL:
      return {
        ...state,
        isLogin: false,
      };
    default:
      return state;
  }
};
export default user;
