import React, { useEffect } from "react";
import PropTypes from "prop-types";
import { getPollRq } from "../../redux/action";
import { connect } from "react-redux";
import "./style.scss";
import { Link, Redirect } from "react-router-dom";
import Addpoll from "../add";

Poll.propTypes = {};

function Poll(props) {
  useEffect(() => props.getPoll(), []);

  const showListPoll = () => {
    if (props.poll.length > 0) {
      return props.poll[0].map((item, i) => {
        return (
          <Link className="poll__item" to={`/polldetail/${item.id}`} key={i}>
            <p>{item.content}</p>
            <div>
              <Link to={`/vote/${item.id}`}>
                <i class="fa fa-check-circle" aria-hidden="true"></i>
              </Link>
              <Link to={`/poll-edit/${item.id}`}>
                <i class="fa fa-pencil-square" aria-hidden="true"></i>
              </Link>
              <i class="fa fa-trash-o" aria-hidden="true"></i>
            </div>
          </Link>
        );
      });
    }
  };

  return (
    <>
      {props.islogin.isLogin === false ? (
        <Redirect to="/"></Redirect>
      ) : (
        <div className="poll">
          <div className="poll__form">
            <Addpoll></Addpoll>
          </div>
          {showListPoll()}
        </div>
      )}
    </>
  );
}

const mapStateToProps = (state) => {
  return {
    poll: state.poll,
    islogin: state.user,
  };
};
const mapDispatchToProps = (dispatch) => {
  return {
    getPoll: () => {
      dispatch(getPollRq());
    },
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(Poll);
